puts "Combien d'handle contiennent un numéro ?"
# puts journalist.select { |i| i =~ /\d/}.count
# code simplifié via les regexp

# Loop conditionnelle verifiant dans chaque item de l'array la présence d'un chiffre et le convertit en string. Si correct, la variable nombre sera incrémenté.
nombre = 0
journalist.each do |i|
  for x in 0..9
    if i.include?(x.to_s) == true
      nombre+=1
      break
    end
  end
end

# affiche nombre. nombre étant le nombre d'handles contenant un numéro
puts nombre

puts "Combien de Aude ?"
# vérifie et compte les occurences de "aude" pour chaque item de l'array
puts journalist.select { |i| i.include?('aude')}.count

puts "Combien commencent par une majuscule ?"
# puts journalist.select { |i| ( i[1] =~ /[A-Z]/ )}.count
# code simplifié via les regexp

# Loop conditionnelle verifiant la présence d'une majuscule sur la première lettre de chaque item de l'array. Si correct, la variable first_maj sera incrémenté.
first_maj = 0
journalist.each do |i|
  for x in ('A'..'Z')
    if i[1].include?(x) == true
      first_maj+=1
      break
    end
  end
end

# affiche first_maj. first_maj étant le nombre d'item commençant par une majuscule
puts first_maj

puts "Combien contiennent une majuscule ?"
# puts journalist.select { |i| ( i =~ /[A-Z]/ )}.count
# code simplifié via les regexp

# Loop conditionnelle verifiant la présence d'une majuscule sur dans chaque item de l'array. Si correct, la variable maj sera incrémenté.
maj = 0
journalist.each do |i|
  for x in ('A'..'Z')
    if i.include?(x) == true
      maj+=1
      break
    end
  end
end

# affiche maj. maj étant le nombre d'item contenant une majuscule
puts maj

puts "Combien de _ dans tous les pseudos confondus ?"
#puts journalist.find_all { |i| i.include?("_")}.count

# Loop conditionnelle verifiant et comptant le nombre de "_" dans la totalité de l'array. Si correct, la variable under_ sera incrémenté.
under_ = 0
journalist.each do |i|
  j = i.length
  for x in (0..j)
    if i[x] == '_'
      under_ += 1
    end
  end
end

# affiche under_. under_ étant le nombre de fois que "_" apparait 
puts under_


